def categorise_discount(discount):
    if 0 <= discount <= 20:
        return "0 - 20"
    elif 20 < discount <= 40:
        return "20 - 40"
    elif 40 < discount <= 60:
        return "40 - 60"
    elif 60 < discount <= 80:
        return "60 - 80"
    elif discount > 80:
        return "> 80"
    else:
      return "Invalid"

df['Discount Categories'] = df['discount_percentage'].apply(categorise_discount)
